sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//modify the iff to return if the new value is same as old value
	//this to stop unnecessary runs
	if (isLoading || newValue === '' || newValue == oldValue) {
		return;
	}

	//Type appropriate comment here, and begin script below
	//instantiate the GetEmailAddress script include
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	//specify the getEmail mehtod
	getEmailAddr.addParam('sysparm_name','getEmail');
	//pass the Requested for sys_id
	getEmailAddr.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	//send to the server
	getEmailAddr.getXML(populateEmailField);

	//when the response is back from the server...
	//now we define the pupulate email resp

	function populateEmailField(response){
		//extract the email address from the response body
		//set a new value in the email field
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.serValue('u_requested_for_email',emailFromScriptInclude);

	}


}
