sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	//Define the getEmail Function
	//Create a Gliderecord for the UserTable
	//use the sysparm user id passed from the client side to retreive a record from the user table
	//return the email address for the requested record
	getEmail: function(){
		//here we name a function within the prototype object that is hooked to the abstract ajax processor

		var userRecord = new GlideRecord("sys_user");
		//within the function we initialize a glide record called user Record...

		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email +'';
		//we pop the userrecord using get from sysparm which we will pull from the client script...
	},
	type:  'GetEmailAddress'
	//what is the type all about - have to ask more on this...

});
